-- Display summary of inserted data
SELECT 'Clubs' as table_name, COUNT(*) as count FROM clubs
UNION ALL
SELECT 'Players' as table_name, COUNT(*) as count FROM players
UNION ALL
SELECT 'Seasons' as table_name, COUNT(*) as count FROM seasons
UNION ALL
SELECT 'Rounds' as table_name, COUNT(*) as count FROM rounds
UNION ALL
SELECT 'Matches' as table_name, COUNT(*) as count FROM matches
UNION ALL
SELECT 'Player Performances' as table_name, COUNT(*) as count FROM player_performances;

-- Display players with their clubs and points
SELECT 
    p.name as player_name,
    p.position,
    c.name as club_name,
    p.price,
    p.total_points
FROM players p
JOIN clubs c ON p.club_id = c.id
ORDER BY p.position, p.total_points DESC;
